import React, { Component } from "react";

class FormFilms extends Component {
    constructor(props) {
        super(props);
        this.state = {
            movieName: " ",
            posterURL: " ",
            comment: " "
        };
        this.onChange = this.onChange.bind(this);
        this.submitForm = this.submitForm.bind(this);
    }

    submitForm(e) {
        e.preventDefault();
        const config = {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(this.state)
        };

        const url = "http://campus-bordeaux.ovh:3001/api/quests/movies/";

        fetch(url, config)
            .then(res => res.json())
            .then(res => {
                if (res.error) {
                    alert(res.error);
                } else {
                    alert(`Film ajouté avec l'ID ${res}!`);
                }
            })
            .catch(e => {
                console.error(e);
                alert("Erreur lors de l'ajout d'un film.");
            });
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    render() {
        return (
            <div className="FormMovie">
                <h1>Saisi d'un Film</h1>

                <form onSubmit={this.submitForm}>
                    <fieldset>
                        <legend>Informations</legend>
                        <div className="form-data">
                            <label htmlFor="movieName">Movie Name</label>
                            <input type="text" id="movieName" name="movieName" onChange={this.onChange} value={this.state.movieName} />
                        </div>

                        <div className="form-data">
                            <label htmlFor="posterURL">URL poster</label>
                            <input type="text" id="posterURL" name="posterURL" onChange={this.onChange} value={this.state.posterURL} />
                        </div>

                        <div className="form-data">
                            <label htmlFor="comment">Comment</label>
                            <input type="textarea" id="comment" name="comment" onChange={this.onChange} value={this.state.comment} />
                        </div>
                        <hr />
                        <div className="form-data">
                            <input type="submit" value="Envoyer" />
                        </div>
                    </fieldset>
                </form>
            </div>
        );
    }
}

export default FormFilms;
